import React, { useEffect, useState } from "react";
import logo from "./logo.png";
import axios from "axios";
import "./user_register.css";
// import {  useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
// import userRegistrationAction from "./actions/user_register_actions";
import Loading from "./loading";

import { useFormik } from 'formik';
import * as Yup from 'yup';
const UserRegister = () => {
  const [countries, setCountries] = useState([]);
  const [states, setStates] = useState([]);
  useEffect(() => {
    // Fetch countries data from the API using Axios
    axios
      .get("https://restcountries.com/v3.1/all")
      .then((response) => {
        console.log(response.data);
        const sortedCountries = response.data.sort((a, b) =>
          a.name.common.localeCompare(b.name.common)
        );
        setCountries(sortedCountries);
      })
      .catch((error) => console.error("Error fetching countries:", error));
  }, []);

  const fetchStatesByCountry = (countryName) => {
    axios
      .get(`https://restcountries.com/v3.1/name/${countryName}/subdivisions`)
      .then((response) => console.log(response))
      .catch((error) => console.error("Error fetching states:", error));
  };

  const initialValues = {
    email: localStorage.getItem("email") ,
    fullName: "",
    phoneNumber: "",
    gender: "",
    password: "",
    occupation: "",
    pincode: "",
    landmark: "",
    state: "",
    country: "",
    confirmPassword: "",
  };


  const validationSchema = Yup.object({
    email: Yup.string().email('Invalid email address').required('Email is required'),
    fullName: Yup.string().required('Full Name is required'),
    phoneNumber: Yup.string()
      .min(8, 'Phone number must be at least 8 characters')
      .max(13, 'Phone number must be at most 13 characters')
      .required('Phone number is required'),
    gender: Yup.string().required('Gender is required'),
    password: Yup.string().min(8, 'Password must be at least 8 characters').required('Password is required'),
    occupation: Yup.string().required('Occupation is required'),
    pincode: Yup.string().required('PIN Code is required'),
    landmark: Yup.string().required('Landmark is required'),
    state: Yup.string().required('State is required'),
    country: Yup.string().required('Country is required'),
    confirmPassword: Yup.string()
      .oneOf([Yup.ref('password'), null], 'Passwords must match')
      .required('Confirm password is required'),
  });

  const formik = useFormik({
    initialValues,
    validationSchema,
    onSubmit: (values) => {
      // Handle form submission with the form values
      console.log(values);
    },
  });
  const handleChange = (e) => {
    formik.handleChange(e);
  };

  // const navigate = useNavigate()
  const dispatch = useDispatch();
  const userRegisterSelector = useSelector((state) => state.userRegister);
  const { loading, error } = userRegisterSelector;

  useEffect(() => {
    if (error) {
      console.log(error);
    }
  }, [error]);
  const handleUserRegister = async () => {
    // Omitting confirmPassword from the logged data
    const { confirmPassword, ...userData } = formik.values;
    console.log(userData);

    // dispatch(userRegistrationAction(formData));
    // try {
    //   const response = await axios.post(
    //     "http://localhost:1213/user/register-user",
    //     formData
    //   );

    //   console.log("Registration successful:", response.data);
    //   navigate("/")
    // } catch (error) {
    //   console.error("Registration failed:", error);
    // }
  };
  return (
    <>
      <nav className="navbar">
        <div className="navbar_logo">
          <img src={logo} alt="" width="270px" />
        </div>
      </nav>
      <div className="send_email">
        <div className="send_email_left">
          <img
            src="https://res.cloudinary.com/notesprofile/image/upload/v1695574487/Screenshot_1445-03-09_at_15.08.56_apbijy.png"
            alt=""
          />
        </div>
        {loading && <Loading />}
        <div className="send_email_right">
          <div className="user_register_content">
            <h2>USER &nbsp; DETAILS </h2>

            <form onSubmit={formik.handleSubmit}>
              <input
                autoComplete="off"
                type="text"
                placeholder="Full Name"
                name="fullName"
                value={formik.values.fullName}
                onChange={formik.handleChange}
              />{formik.touched.email && formik.errors.email && (
                <div style={{ color: 'red', marginLeft:"4em"}}>{formik.errors.email}</div>

                            )}

              <input
                autoComplete="off"
                type="text"
                placeholder="Phone number"
                name="phoneNumber"
                value={formik.values.phoneNumber}
                onChange={formik.handleChange}
              />

              <select
                name="gender"
                value={formik.values.gender}
                onChange={formik.handleChange}
              >
                <option value="">Select Gender</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Other">Other</option>
              </select>

              <select
                name="occupation"
                value={formik.values.occupation}
                onChange={formik.handleChange}
              >
                <option value="">Select Occupation</option>
                <option value="Student">Student</option>
                <option value="Teacher">Teacher</option>
                <option value="Engineer">Engineer</option>
                <option value="Doctor">Doctor</option>
                <option value="Artist">Artist</option>
                <option value="Self Employed">Self Employed</option>
              </select>

              <input
                autoComplete="off"
                type="text"
                placeholder="Landmark"
                name="landmark"
                value={formik.values.landmark}
                onChange={formik.handleChange}
              />

              <input
                autoComplete="off"
                type="text"
                placeholder="PIN CODE"
                name="pincode"
                value={formik.values.pincode}
                onChange={formik.handleChange}
              />

              <input
                autoComplete="off"
                type="password"
                placeholder="Password"
                name="password"
                value={formik.values.password}
                onChange={formik.handleChange}
              />

              <input
                autoComplete="off"
                type="password"
                placeholder="Confirm password"
                name="confirmPassword"
                value={formik.values.confirmPassword}
                onChange={formik.handleChange}
              />

              <select
                name="country"
                value={formik.values.country}
                onChange={formik.handleChange}
              >
                <option value="">Select Country</option>
                {/* ... options for countries ... */}
              </select>

              <select
                name="state"
                value={formik.values.state}
                onChange={formik.handleChange}
              >
                <option value="">Select State</option>
                {/* ... options for states ... */}
              </select>
            </form>
            <button onClick={handleUserRegister}>Register</button>
          </div>
        </div>
      </div>
    </>
  );
};

export default UserRegister;
