import React, { useState } from 'react'
import "./send_email.css"
import NavbarAuth from './navbar_auth'

const SendEmail = () => {

  const [sentOtp,setSentOtp]= useState(false);
  const [otp, setOtp] = useState('');
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const enteredOtp = e.target.value;

    // Ensure that the entered value is a 4-digit number
    if (/^\d{4}$/.test(enteredOtp)) {
      setOtp(enteredOtp);
      setMessage('');
    } else {
      setMessage('Please enter a valid 4-digit OTP.');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // You can add your OTP verification logic here
    // For this example, we'll simply display the entered OTP
    alert(`Entered OTP: ${otp}`);
  };
  return (
    <>
        <NavbarAuth/>

        <div className="send_email">

        <div className="send_email_left">
    <img src="https://res.cloudinary.com/notesprofile/image/upload/v1695574487/Screenshot_1445-03-09_at_15.08.56_apbijy.png" alt="" />
        </div>
        <div className="send_email_right">
    {sentOtp ?
          <div className="send_email_content">

          <h2>USER &nbsp; REGISTER</h2>
          <br />
          <svg xmlns="http://www.w3.org/2000/svg" height="34" viewBox="0 0 24 24" width="34" style={{position:"relative",left:"66px",top:"-3px"}}><path d="M0 0h24v24H0z" fill="none"/><path d="M20 4H4c-1.1 0-1.99.9-1.99 2L2 18c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2zm0 4l-8 5-8-5V6l8 5 8-5v2z"/></svg>
          <input type="text" placeholder="Email address"/><br />

          <button>Submit</button>
          <br />

        <p>--------OR--------</p>
    <p className='span'>Already have an account? &nbsp; &nbsp;<a href='#'>Sign in</a></p>
          </div> : 
          <div className="otp_content">
  <h2>OTP Verification</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Enter OTP:
          <input
            type="text"
            value={otp}
            onChange={handleChange}
            maxLength="4"
          />
        </label>
        <button type="submit">Submit</button>
      </form>
      <p>{message}</p>
          </div>
          }
        </div>
        </div>
    </>
  )
}

export default SendEmail
