import React, { useState } from "react";
import Navbar from "./navbar";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Body from "./body";
import Brands from "./brands";
import Category from "./category";
import FeaturedProducts from "./featured_products";
import Footer from "./footer";
import HelpCenter from "./help_center";
import NewsLetter from "./newsletter";
import Services from "./services";
import SpecialOffer from "./special_offers";
import TodaySpecial from "./today_special";
import WeeklyPopular from "./weeklyPopular";

function App() {
  const scrollToSection = (sectionId) => {
    // Implement your scroll-to-section logic here
    console.log(`Scrolling to section: ${sectionId}`);
  };

  return (
    <>
      <div className="app">
        <Router>
          <Navbar scrollToSection={scrollToSection} />

          <Routes>
            <Route path="/" element={<Body />} />
            <Route path="/category" element={<Category />} />
            <Route path="/specialOffer" element={<SpecialOffer />} />
            <Route path="/services" element={<Services />} />
            <Route path="/weeklyPopular" element={<WeeklyPopular />} />
          </Routes>

          <TodaySpecial />
          <Brands />
          <FeaturedProducts />
          <NewsLetter />
          <HelpCenter />
          <Footer />
        </Router>
      </div>
    </>
  );
}

export default App;
