            <br />
            <p style={{ marginTop: "40px" }}>Shipping Address :</p>
            <span>Lorem ipsum dolor sit amet, consectetur adipisicing.</span>
            <button>Add to Cart</button>
          </div>
        </div>
      </div>
        </div>
        <DashboardRight />
      </div>
    </>
  );
};

export default SellerSingleProduct;
