import React, { useEffect, useState } from "react";
import logo from "./logo.png";
import axios from "axios";
import "./user_register.css";
// import {  useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import userRegistrationAction from "./actions/user_register_actions";
import Loading from "./loading";

const UserRegister = () => {
  const [countries, setCountries] = useState([]);
  const [states, setStates] = useState([]);

  useEffect(() => {
    // Fetch countries data from the API using Axios
    axios
      .get("https://api.countrystatecity.in/v1/countries")
      .then((response) => {
        const sortedCountries = response.data.sort((a, b) =>
          a.name.common.localeCompare(b.name.common)
        );
        setCountries(sortedCountries);
      })
      .catch((error) => console.error("Error fetching countries:", error));
  }, []);

  const fetchStatesByCountry = (countryName) => {
    axios
      .get(
        `https://restcountries.com/v3.1/name/${countryName}/subdivisions`
      )
      .then((response) => console.log(response))
      .catch((error) => console.error("Error fetching states:", error));
  };

  const [formData, setFormData] = useState({
    email: localStorage.getItem("email"),
    fullName: "",
    phoneNumber: "",
    gender: "",
    password: "",
    occupation: "",
    pincode: "",
    landmark: "",
    state: "",
  });
  // const navigate = useNavigate()
  const dispatch = useDispatch();
  const userRegisterSelector = useSelector((state) => state.userRegister);
  const { loading, error } = userRegisterSelector;
  const handleChange = (e) => {
    const { name, value } = e.target;
    let updatedValue = value;

    switch (name) {
      case "email":
        // Basic email validation
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(value)) {
          console.error("Invalid email address");
          updatedValue = "";
        }
        break;

      case "password":
        const passwordRegex = /^[a-zA-Z\s]+$/;
        if (!passwordRegex.test(value)) {
          console.error("Invalid full name");
          updatedValue = "";
        }
        break;

      case "fullName":
        const fullNameRegex = /^[a-zA-Z\s]+$/;
        if (!fullNameRegex.test(value)) {
          console.error("Invalid full name");
          updatedValue = "";
        }
        break;

      case "phoneNumber":
        const phoneNumberRegex = /^\d+(-\d+)*$/;
        if (!phoneNumberRegex.test(value)) {
          console.error("Invalid phone number");
          updatedValue = "";
        }
        break;

      case "gender":
        if (!["Male", "Female", "Other"].includes(value)) {
          console.error("Invalid gender");
          updatedValue = "";
        }
        break;

      case "occupation":
        // Validate that the value is one of the predefined options
        const validOccupations = [
          "Student",
          "Teacher",
          "Engineer",
          "Doctor",
          "Artist",
          "Self Employed",
        ];
