import React, { useEffect, useState } from "react";
import logo from "./logo.png";
import "./user_register.css";
// import {  useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import userRegistrationAction from "./actions/user_register_actions";
import Loading from "./loading";

const UserRegister = () => {
  const [formData, setFormData] = useState({
    email: localStorage.getItem("email"),
    fullName: "",
    phoneNumber: "",
    gender: "",
    password: "",
    occupation: "",
    pincode: "",
    landmark: "",
    state: "",
  });
  // const navigate = useNavigate()
  const dispatch = useDispatch();
  const userRegisterSelector = useSelector((state) => state.userRegister);
  const { loading, error } = userRegisterSelector;
  const handleChange = (e) => {
    const { name, value } = e.target;
    let updatedValue = value;

    switch (name) {
      case 'email':
        // Basic email validation
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(value)) {
          console.error('Invalid email address');
          updatedValue = '';
        }
        break;

      case 'password':
        const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*()_+])[A-Za-z\d!@#$%^&*()_+]{8,}$/;
        if (!passwordRegex.test(value)) {
          console.error('Invalid Password');
          updatedValue = '';
        }
        break;

      case 'fullName':
        const fullNameRegex = /^[a-zA-Z\s]+$/;
        if (!fullNameRegex.test(value)) {
          console.error('Invalid full name');
          updatedValue = '';
        }
        break;

      case 'phoneNumber':
        const phoneNumberRegex = /^\d+(-\d+)*$/;
        if (!phoneNumberRegex.test(value)) {
          console.error('Invalid phone number');
          updatedValue = '';
        }
        break;

      case 'gender':
        if (!['Male', 'Female'].includes(value)) {
          console.error('Invalid gender');
          updatedValue = '';
        }
        break;

      case 'occupation':
        const occupationRegex = /^[a-zA-Z\s]+$/;
        if (!occupationRegex.test(value)) {
          console.error('Invalid occupation');
          updatedValue = '';
        }
        break;

      case 'pincode':
        const pincodeRegex = /^\d+$/;
        if (!pincodeRegex.test(value)) {
          console.error('Invalid pincode');
          updatedValue = '';
        }
        break;

      case 'landmark':
        const landmarkRegex = /^[a-zA-Z0-9\s]+$/;
        if (!landmarkRegex.test(value)) {
          console.error('Invalid landmark');
          updatedValue = '';
        }
        break;

      case 'state':
        // State should only contain letters and spaces
        const stateRegex = /^[a-zA-Z\s]+$/;
