import React, { useState } from "react";
import DashboardLeft from "./dashboard_left";
import DashboardRight from "./dashboard_right";
import "./seller_add_product.css";

const SellerAddProduct = () => {
  const [productData, setProductData] = useState({
    productName: "",
    productDescription:"",
    marketPrice: "",
    offerPrice: "",
    rating: "",
    manufacturerAddress: "",
    size: "",
    productDetails: "",
    category: "",
  });

  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setSelectedImage(file);
      const formData = new FormData();
    formData.append('image', selectedImage);

    // Make a POST request to your backend to upload the image
    fetch('http://your-backend-url/upload-image', {
      method: 'POST',
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Image URL:', data.imageUrl);
        // You can do something with the imageURL, like displaying the image or saving it to your form data.
      })
      .catch((error) => {
        console.error('Image upload error:', error);
      });


  };
  const handleChange = (e) => {
    const { name, value } = e.target;
    setProductData({
      ...productData,
      [name]: value,
    });
  };
  return (
    <>
      <div className="seller_dashboard">
        <DashboardLeft />
        <div className="sidebar_mid">
          <h1 style={{ padding: "20px 0" }}>Add Product</h1>
          <div className="seller_add_product">
            <div className="seller_add_product_left">
              <input type="text" placeholder="Product Name" />
              <textarea
                name=""
                id=""
                cols="27"
                rows="2"
                placeholder="Product description"
              ></textarea>
              <input type="text" placeholder="Market Price" />
              <input type="text" placeholder="Offer Price" />
              <input type="text" placeholder="Rating (out of 5)" />
              <textarea
                name=""
                id=""
                cols="27"
                rows="2"
                placeholder="Manufacturer Address"
              ></textarea>
              <input type="text" placeholder="Size (optional)" />
              <textarea
                name=""
                id=""
                cols="27"
                rows="2"
                placeholder="Product Details"
              ></textarea>

              <select id="category" name="category" style={{ width: "86.5%" }}>
                <option value="">Select Category</option>
                <option value="gadgets">Gadgets</option>
                <option value="fashion">Fashion</option>
                <option value="education">Education</option>
                <option value="beauty">Beauty</option>
                <option value="fitness">Fitness</option>
                <option value="sneaker">Sneaker</option>
              </select>

              <input
                type="file"
                accept=".png"
                placeholder="Image"
                name="product_image"
                onChange={handleImageUpload} 
              />
              <br />
              <span>Image of product</span>
              <button>Submit</button>
            </div>
            <div className="seller_add_product_right">
              <img
                src="https://res.cloudinary.com/notesprofile/image/upload/v1695596240/Screenshot_1445-03-09_at_15.13.58_r5fqvz.png"
                height={"800px"}
                width={"100%"}
                style={{ borderTop: "1.3px solid #333" }}
                alt=""
              />
            </div>
          </div>
        </div>
        <DashboardRight />
      </div>
    </>
  );
};

export default SellerAddProduct;
