import React, { useState } from "react";
import "./send_email.css";
import NavbarAuth from "./navbar_auth";

const SendEmail = () => {
  const [sentOtp, setSentOtp] = useState(false);
  const [otp, setOTP] = useState(["", "", "", ""]);

  const handleChange = (e, index) => {
    const value = e.target.value;

    if (/^\d*$/.test(value) && value.length <= 1) {
      const newOTP = [...otp];
      newOTP[index] = value;
      setOTP(newOTP);

      // Move to the next input box if a digit is entered
      if (value !== "" && index < 3) {
        document.getElementById(`otp-input-${index + 1}`).focus();
      }
    }
  };
  return (
    <>
      <NavbarAuth />

      <div className="send_email">
        <div className="send_email_left">
          <img
            src="https://res.cloudinary.com/notesprofile/image/upload/v1695574487/Screenshot_1445-03-09_at_15.08.56_apbijy.png"
            alt=""
          />
        </div>
        <div className="send_email_right">
          {sentOtp ? (
            <div className="send_email_content">
              <h2>USER &nbsp; REGISTER</h2>
              <br />
              <svg
                xmlns="http://www.w3.org/2000/svg"
                height="34"
                viewBox="0 0 24 24"
                width="34"
                style={{ position: "relative", left: "66px", top: "-3px" }}
              >
                <path d="M0 0h24v24H0z" fill="none" />
                <path d="M20 4H4c-1.1 0-1.99.9-1.99 2L2 18c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2zm0 4l-8 5-8-5V6l8 5 8-5v2z" />
              </svg>
              <input type="text" placeholder="Email address" />
              <br />

              <button>Submit</button>
              <br />

              <p>--------OR--------</p>
              <p className="span">
                Already have an account? &nbsp; &nbsp;<a href="#">Sign in</a>
              </p>
            </div>
          ) : (
            <div className="otp_content">
              <h2>OTP Verification</h2>
              <form>
                <input
                  type="text"
                  id="otp-input-0"
                  value={otp[0]}
                  onChange={(e) => handleChange(e, 0)}
                  maxLength="1"
                  autoComplete="off"
                  className="small-input"
                />
                <input
                  type="text"
                  id="otp-input-1"
                  value={otp[1]}
                  onChange={(e) => handleChange(e, 1)}
                  maxLength="1"
                  autoComplete="off"
                  className="small-input"
                />
                <input
                  type="text"
                  id="otp-input-2"
                  value={otp[2]}
                  onChange={(e) => handleChange(e, 2)}
                  className="small-input"
                  autoComplete="off"
                  maxLength="1"
                />
                <input
                  type="text"
                  id="otp-input-3"
                  value={otp[3]}
                  onChange={(e) => handleChange(e, 3)}
                  maxLength="1"
                  autoComplete="off"
                  className="small-input"
                />
              </form>
              <button onClick={handleVerifyClick}>Verify</button>
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default SendEmail;
