import React, { useState } from "react";
import Navbar from "./navbar";
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Body from "./body";
import Brands from "./brands";
import Category from "./category";
import FeaturedProducts from "./featured_products";
import Footer from "./footer";
import HelpCenter from "./help_center";
import NewsLetter from "./newsletter";
import Services from "./services";
import SpecialOffer from "./special_offers";
import TodaySpecial from "./today_special";
import WeeklyPopular from "./weeklyPopular";

function App() {
  const [activeSection, setActiveSection] = useState(null);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
      setActiveSection(sectionId);
    }
  };

  return (
    <>
      <div className="app">
        <Router>
          <Navbar scrollToSection={scrollToSection} />

          <Routes>
            <Route
              path="/"
              element={<Body setActiveSection={setActiveSection} />}
            />
            <Route
              path="/category"
              element={<Category setActiveSection={setActiveSection} />}
            />
            <Route
              path="/specialOffer"
              element={<SpecialOffer setActiveSection={setActiveSection} />}
            />
            <Route
              path="/services"
              element={<Services setActiveSection={setActiveSection} />}
            />
            <Route
              path="/weeklyPopular"
              element={<WeeklyPopular setActiveSection={setActiveSection} />}
            />
          </Routes>

          <TodaySpecial />
          <Brands />
          <FeaturedProducts />
          <NewsLetter />
          <HelpCenter />
          <Footer />
        </Router>
      </div>
    </>
  );
}

export default App;
