import React,{useState} from "react";
import Navbar from "./navbar";
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Body from "./body";
import Brands from "./brands";
import Category from "./category";
import FeaturedProducts from "./featured_products";
import Footer from "./footer";
import HelpCenter from "./help_center";
import NewsLetter from "./newsletter";
import Services from "./services";
import SpecialOffer from "./special_offers";
import TodaySpecial from "./today_special";
import WeeklyPopular from "./weeklyPopular";

function App() {


  const [activeSection, setActiveSection] = useState(null);
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
      setActiveSection(sectionId);
    }
  };

  return (
    <>
      <div className="app">
<Router>

      <Navbar scrollToSection={scrollToSection} />

      <Routes>
          <Route
            path="/"
            element={
              <div id="body" className={activeSection === 'body' ? 'active' : ''}>
                <Body />
              </div>
            }
          />
          <Route
            path="/category"
            element={
              <div id="category" className={activeSection === 'category' ? 'active' : ''}>
                <Category />
              </div>
            }
          />
          <Route
            path="/specialOffer"
            element={
              <div id="specialOffer" className={activeSection === 'specialOffer' ? 'active' : ''}>
                <SpecialOffer />
              </div>
            }
          />
          <Route
            path="/services"
            element={
              <div id="services" className={activeSection === 'services' ? 'active' : ''}>
                <Services />
              </div>
            }
          />
          <Route
            path="/weeklyPopular"
            element={
              <div id="weeklyPopular" className={activeSection === 'weeklyPopular' ? 'active' : ''}>
                <WeeklyPopular />
              </div>
            }
          />
        </Routes>
        <TodaySpecial />
        <Brands />
        <FeaturedProducts />
        <NewsLetter />
        <HelpCenter />
        <Footer />
      </Router>
      </div>
    </>
  );
}

export default App;
