// Import the useHistory hook from react-router-dom
import { useHistory } from 'react-router-dom';

const verifyOtpAction = (userEmail, otpValue) => async (dispatch) => {
  const history = useHistory(); // Get the history object

  try {
    dispatch({ type: VERIFY_OTP_REQUEST });
    const config = {
      headers: {
        'Content-type': 'application/json',
      },
    };

    const response = await axios.post("http://localhost:1213/user/verify-otp", {
      email: userEmail,
      otp: otpValue,
    }, config);

    dispatch({ type: VERIFY_OTP_SUCCESS, payload: response });

    // Navigate to the "/user-register" route upon successful OTP verification
    history.push("/user-register");

  } catch (error) {
    console.log("Error response from server:", error);
    let errorMessage = "An error occurred"; // Default error message

    if (error.response && error.response.data.error) {
      errorMessage = error.response.data.error; // Use the error message from the server if available
    }

    dispatch({ type: VERIFY_OTP_FAILURE, payload: errorMessage });
  }
};

export default verifyOtpAction;
