  const { product, loading, error } = getProductsByIdSelector;
  const [fetched, setFetched] = useState(false);

  useEffect(() => {
    // Check if the product has already been fetched
    if (!fetched) {
      dispatch(getProductByIdAction(id));
      setFetched(true);
    }
    console.log("",product)
  }, [fetched, dispatch, id]);



