import React, { useState } from "react";
import DashboardLeft from "./dashboard_left";
import DashboardRight from "./dashboard_right";
import "./seller_add_product.css";
import axios from "axios"

const SellerAddProduct = () => {
  // const [productData, setProductData] = useState({
  //   productName: "",
  //   productDescription:"",
  //   marketPrice: "",
  //   offerPrice: "",
  //   rating: "",
  //   manufacturerAddress: "",
  //   size: "",
  //   productDetails: "",
  //   category: "",
  // });

  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageUpload = async(e) => {
    const file = e.target.files[0];
    setSelectedImage(file);

    const formData = new FormData();
    formData.append("file", selectedImage);
    formData.append("upload_preset", "NotesApp"); // Updated upload_preset
    formData.append("cloud_name", "notesprofile"); // Updated cloud_name

    try {
      const response = await axios.post(
        'https://api.cloudinary.com/v1_1/notes_profile/image/upload', // Updated cloudinary URL
        formData, 
        {
          headers: {
            'Content-Type': 'multipart/form-data', // Ensure the correct content type
          },
        }
      );

      // Extract the secure_url from the response data
      const imageUrl = response.data.secure_url;

      // You can now use imageUrl as needed, e.g., set it in your state
      console.log('Image URL:', imageUrl);

      return imageUrl;
    } catch (error) {
      console.error('Image upload error:', error);
      throw error; // You can handle the error as needed
    }



// // Assuming you have a formData object with the image
// const formData = new FormData();
// formData.append('image', selectedImage); // Replace selectedImageFile with your image file

// // Make a POST request to your backend to upload the image
// axios.post('http://localhost:1213/cloudinary/upload-image', formData)
//   .then((response) => {
//     console.log('Image URL:', response.data.imageUrl);
//     // You can do something with the imageURL, like displaying the image or saving it to your form data.
//   })
//   .catch((error) => {
//     console.error('Image upload error:', error);
//   });
  };
  // const handleChange = (e) => {
  //   const { name, value } = e.target;
  //   setProductData({
  //     ...productData,
  //     [name]: value,
  //   });
  // };
  return (
    <>
      <div className="seller_dashboard">
        <DashboardLeft />
        <div className="sidebar_mid">
          <h1 style={{ padding: "20px 0" }}>Add Product</h1>
          <div className="seller_add_product">
            <div className="seller_add_product_left">
              <input type="text" placeholder="Product Name" />
              <textarea
                name=""
                id=""
                cols="27"
                rows="2"
                placeholder="Product description"
              ></textarea>
              <input type="text" placeholder="Market Price" />
              <input type="text" placeholder="Offer Price" />
              <input type="text" placeholder="Rating (out of 5)" />
              <textarea
                name=""
                id=""
                cols="27"
                rows="2"
                placeholder="Manufacturer Address"
              ></textarea>
              <input type="text" placeholder="Size (optional)" />
              <textarea
                name=""
                id=""
                cols="27"
                rows="2"
                placeholder="Product Details"
              ></textarea>

              <select id="category" name="category" style={{ width: "86.5%" }}>
                <option value="">Select Category</option>
                <option value="gadgets">Gadgets</option>
                <option value="fashion">Fashion</option>
                <option value="education">Education</option>
                <option value="beauty">Beauty</option>
                <option value="fitness">Fitness</option>
                <option value="sneaker">Sneaker</option>
              </select>

              <input
                type="file"
                accept=".png"
                placeholder="Image"
                name="product_image"
                onChange={handleImageUpload} 
              />
              <br />
              <span>Image of product</span>
              <button>Submit</button>
            </div>
            <div className="seller_add_product_right">
              <img
                src="https://res.cloudinary.com/notesprofile/image/upload/v1695596240/Screenshot_1445-03-09_at_15.13.58_r5fqvz.png"
                height={"800px"}
                width={"100%"}
                style={{ borderTop: "1.3px solid #333" }}
                alt=""
              />
            </div>
          </div>
        </div>
        <DashboardRight />
      </div>
    </>
  );
};

export default SellerAddProduct;
